sqls.executeSql:
  
	update gl_period
	   set 
		period_name = ${@period_name},
		priority = ${@priority}		
	 where period_id = ${@period_id} 
	   and book_set_id = ${@book_set_id}

  
	update gl_period
	   set 
		status = 'Y'
	 where period_id = ${@period_id} 
	  and book_set_id = ${@book_set_id}

  
	update gl_period
	   set 
		status = 'N'
	 where period_id = ${@period_id} 
	  and book_set_id = ${@book_set_id}
